function Compostage_insert(){
	global $Translation;

	// mm: can member insert record?
	$arrPerm=getTablePermissions('Compostage');
	if(!$arrPerm[1]){
		return false;
	}

	$data['date'] = intval($_REQUEST['dateYear']) . '-' . intval($_REQUEST['dateMonth']) . '-' . intval($_REQUEST['dateDay']);
	$data['date'] = parseMySQLDate($data['date'], '1');
	$data['lieu_compostage'] = makeSafe($_REQUEST['lieu_compostage']);
		if($data['lieu_compostage'] == empty_lookup_value){ $data['lieu_compostage'] = ''; }
	$data['matiere'] = makeSafe($_REQUEST['matiere']);
		if($data['matiere'] == empty_lookup_value){ $data['matiere'] = ''; }
	$data['poids'] = makeSafe($_REQUEST['poids']);
		if($data['poids'] == empty_lookup_value){ $data['poids'] = ''; }
	if($data['lieu_compostage']== ''){
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">" . $Translation['error:'] . " 'Lieu compostage': " . $Translation['field not null'] . '<br><br>';
		echo '<a href="" onclick="history.go(-1); return false;">'.$Translation['< back'].'</a></div>';
		exit;
	}
	if($data['matiere']== ''){
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">" . $Translation['error:'] . " 'Mati&#232;re': " . $Translation['field not null'] . '<br><br>';
		echo '<a href="" onclick="history.go(-1); return false;">'.$Translation['< back'].'</a></div>';
		exit;
	}
	if($data['poids']== ''){
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">" . $Translation['error:'] . " 'Poids': " . $Translation['field not null'] . '<br><br>';
		echo '<a href="" onclick="history.go(-1); return false;">'.$Translation['< back'].'</a></div>';
		exit;
	}

	// hook: Compostage_before_insert
	if(function_exists('Compostage_before_insert')){
		$args=array();
		if(!Compostage_before_insert($data, getMemberInfo(), $args)){ return false; }
	}

	$o = array('silentErrors' => true);
	sql('insert into `Compostage` set       `date`=' . (($data['date'] !== '' && $data['date'] !== NULL) ? "'{$data['date']}'" : 'NULL') . ', `lieu_compostage`=' . (($data['lieu_compostage'] !== '' && $data['lieu_compostage'] !== NULL) ? "'{$data['lieu_compostage']}'" : 'NULL') . ', `matiere`=' . (($data['matiere'] !== '' && $data['matiere'] !== NULL) ? "'{$data['matiere']}'" : 'NULL') . ', `poids`=' . (($data['poids'] !== '' && $data['poids'] !== NULL) ? "'{$data['poids']}'" : 'NULL'), $o);
	if($o['error']!=''){
		echo $o['error'];
		echo "<a href=\"Compostage_view.php?addNew_x=1\">{$Translation['< back']}</a>";
		exit;
	}

	$recID = db_insert_id(db_link());

	// hook: Compostage_after_insert
	if(function_exists('Compostage_after_insert')){
		$res = sql("select * from `Compostage` where `id`='" . makeSafe($recID, false) . "' limit 1", $eo);
		if($row = db_fetch_assoc($res)){
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args=array();
		if(!Compostage_after_insert($data, getMemberInfo(), $args)){ return $recID; }
	}

	// mm: save ownership data
	set_record_owner('Compostage', $recID, getLoggedMemberID());

	return $recID;
}

